import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertAlertSchema, insertAgentSchema, insertVulnerabilitySchema, insertComplianceResultSchema, insertMitreAttackSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Dashboard metrics endpoint
  app.get("/api/dashboard/metrics", async (req, res) => {
    try {
      const metrics = await storage.getDashboardMetrics();
      res.json(metrics);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard metrics" });
    }
  });

  // Agent endpoints
  app.get("/api/agents", async (req, res) => {
    try {
      const agents = await storage.getAgents();
      res.json(agents);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch agents" });
    }
  });

  app.get("/api/agents/status", async (req, res) => {
    try {
      const statusCounts = await storage.getAgentStatusCounts();
      res.json(statusCounts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch agent status" });
    }
  });

  app.get("/api/agents/recent", async (req, res) => {
    try {
      const recentActivity = await storage.getRecentAgentActivity();
      res.json(recentActivity);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch recent agent activity" });
    }
  });

  app.post("/api/agents", async (req, res) => {
    try {
      const validatedData = insertAgentSchema.parse(req.body);
      const agent = await storage.createAgent(validatedData);
      res.status(201).json(agent);
    } catch (error) {
      res.status(400).json({ message: "Invalid agent data" });
    }
  });

  app.get("/api/agents/:id", async (req, res) => {
    try {
      const agent = await storage.getAgent(req.params.id);
      if (!agent) {
        return res.status(404).json({ message: "Agent not found" });
      }
      res.json(agent);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch agent" });
    }
  });

  // Alert endpoints
  app.get("/api/alerts", async (req, res) => {
    try {
      const { search, page, limit } = req.query;
      const filters = {
        search: search as string,
        page: page ? parseInt(page as string) : undefined,
        limit: limit ? parseInt(limit as string) : undefined,
      };
      const alerts = await storage.getAlerts(filters);
      res.json(alerts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch alerts" });
    }
  });

  app.get("/api/alerts/timeline", async (req, res) => {
    try {
      const timeline = await storage.getAlertsTimeline();
      res.json(timeline);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch alerts timeline" });
    }
  });

  app.post("/api/alerts", async (req, res) => {
    try {
      const validatedData = insertAlertSchema.parse(req.body);
      const alert = await storage.createAlert(validatedData);
      res.status(201).json(alert);
    } catch (error) {
      res.status(400).json({ message: "Invalid alert data" });
    }
  });

  app.get("/api/alerts/:id", async (req, res) => {
    try {
      const alert = await storage.getAlert(req.params.id);
      if (!alert) {
        return res.status(404).json({ message: "Alert not found" });
      }
      res.json(alert);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch alert" });
    }
  });

  // Vulnerability endpoints
  app.get("/api/vulnerabilities", async (req, res) => {
    try {
      const vulnerabilities = await storage.getVulnerabilities();
      res.json(vulnerabilities);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch vulnerabilities" });
    }
  });

  app.get("/api/vulnerabilities/distribution", async (req, res) => {
    try {
      const distribution = await storage.getVulnerabilityDistribution();
      res.json(distribution);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch vulnerability distribution" });
    }
  });

  app.post("/api/vulnerabilities", async (req, res) => {
    try {
      const validatedData = insertVulnerabilitySchema.parse(req.body);
      const vulnerability = await storage.createVulnerability(validatedData);
      res.status(201).json(vulnerability);
    } catch (error) {
      res.status(400).json({ message: "Invalid vulnerability data" });
    }
  });

  app.get("/api/vulnerabilities/:id", async (req, res) => {
    try {
      const vulnerability = await storage.getVulnerability(req.params.id);
      if (!vulnerability) {
        return res.status(404).json({ message: "Vulnerability not found" });
      }
      res.json(vulnerability);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch vulnerability" });
    }
  });

  // Compliance endpoints
  app.get("/api/compliance", async (req, res) => {
    try {
      const results = await storage.getComplianceResults();
      res.json(results);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch compliance results" });
    }
  });

  app.get("/api/compliance/overview", async (req, res) => {
    try {
      const overview = await storage.getComplianceOverview();
      res.json(overview);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch compliance overview" });
    }
  });

  app.post("/api/compliance", async (req, res) => {
    try {
      const validatedData = insertComplianceResultSchema.parse(req.body);
      const result = await storage.createComplianceResult(validatedData);
      res.status(201).json(result);
    } catch (error) {
      res.status(400).json({ message: "Invalid compliance data" });
    }
  });

  // MITRE ATT&CK endpoints
  app.get("/api/mitre-attack", async (req, res) => {
    try {
      const mitreData = await storage.getMitreAttackData();
      res.json(mitreData);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch MITRE ATT&CK data" });
    }
  });

  app.post("/api/mitre-attack", async (req, res) => {
    try {
      const validatedData = insertMitreAttackSchema.parse(req.body);
      const mitreData = await storage.createMitreAttackData(validatedData);
      res.status(201).json(mitreData);
    } catch (error) {
      res.status(400).json({ message: "Invalid MITRE ATT&CK data" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type Agent, 
  type InsertAgent,
  type Alert,
  type InsertAlert,
  type Vulnerability,
  type InsertVulnerability,
  type ComplianceResult,
  type InsertComplianceResult,
  type MitreAttackData,
  type InsertMitreAttack,
  type DashboardMetrics,
  type AgentStatusCounts,
  type ComplianceFrameworkScore
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Agent methods
  getAgents(): Promise<Agent[]>;
  getAgent(id: string): Promise<Agent | undefined>;
  createAgent(agent: InsertAgent): Promise<Agent>;
  updateAgent(id: string, agent: Partial<Agent>): Promise<Agent | undefined>;
  getAgentStatusCounts(): Promise<AgentStatusCounts>;
  getRecentAgentActivity(): Promise<Agent[]>;

  // Alert methods
  getAlerts(filters?: { search?: string; page?: number; limit?: number }): Promise<Alert[]>;
  getAlert(id: string): Promise<Alert | undefined>;
  createAlert(alert: InsertAlert): Promise<Alert>;
  getAlertsTimeline(): Promise<any[]>;

  // Vulnerability methods
  getVulnerabilities(): Promise<Vulnerability[]>;
  getVulnerability(id: string): Promise<Vulnerability | undefined>;
  createVulnerability(vulnerability: InsertVulnerability): Promise<Vulnerability>;
  getVulnerabilityDistribution(): Promise<any[]>;

  // Compliance methods
  getComplianceResults(): Promise<ComplianceResult[]>;
  createComplianceResult(result: InsertComplianceResult): Promise<ComplianceResult>;
  getComplianceOverview(): Promise<ComplianceFrameworkScore[]>;

  // MITRE ATT&CK methods
  getMitreAttackData(): Promise<MitreAttackData[]>;
  createMitreAttackData(data: InsertMitreAttack): Promise<MitreAttackData>;

  // Dashboard methods
  getDashboardMetrics(): Promise<DashboardMetrics>;
}

export class MemStorage implements IStorage {
  private agents: Map<string, Agent> = new Map();
  private alerts: Map<string, Alert> = new Map();
  private vulnerabilities: Map<string, Vulnerability> = new Map();
  private complianceResults: Map<string, ComplianceResult> = new Map();
  private mitreAttackData: Map<string, MitreAttackData> = new Map();

  constructor() {
    this.initializeData();
  }

  private initializeData() {
    // Initialize with empty data - will be populated by API
  }

  // Agent methods
  async getAgents(): Promise<Agent[]> {
    return Array.from(this.agents.values());
  }

  async getAgent(id: string): Promise<Agent | undefined> {
    return this.agents.get(id);
  }

  async createAgent(insertAgent: InsertAgent): Promise<Agent> {
    const id = randomUUID();
    const agent: Agent = { 
      ...insertAgent, 
      id,
      lastSeen: new Date()
    };
    this.agents.set(id, agent);
    return agent;
  }

  async updateAgent(id: string, updates: Partial<Agent>): Promise<Agent | undefined> {
    const agent = this.agents.get(id);
    if (!agent) return undefined;

    const updatedAgent = { ...agent, ...updates };
    this.agents.set(id, updatedAgent);
    return updatedAgent;
  }

  async getAgentStatusCounts(): Promise<AgentStatusCounts> {
    const agents = Array.from(this.agents.values());
    return {
      active: agents.filter(a => a.status === 'active').length,
      disconnected: agents.filter(a => a.status === 'disconnected').length,
      pending: agents.filter(a => a.status === 'pending').length,
    };
  }

  async getRecentAgentActivity(): Promise<Agent[]> {
    const agents = Array.from(this.agents.values());
    return agents
      .sort((a, b) => (b.lastSeen?.getTime() || 0) - (a.lastSeen?.getTime() || 0))
      .slice(0, 5);
  }

  // Alert methods
  async getAlerts(filters?: { search?: string; page?: number; limit?: number }): Promise<Alert[]> {
    let alerts = Array.from(this.alerts.values());

    if (filters?.search) {
      const search = filters.search.toLowerCase();
      alerts = alerts.filter(alert => 
        alert.rule.toLowerCase().includes(search) ||
        alert.agentName.toLowerCase().includes(search) ||
        alert.description.toLowerCase().includes(search)
      );
    }

    // Sort by timestamp (newest first)
    alerts.sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0));

    // Apply pagination
    if (filters?.page && filters?.limit) {
      const start = (filters.page - 1) * filters.limit;
      alerts = alerts.slice(start, start + filters.limit);
    }

    return alerts;
  }

  async getAlert(id: string): Promise<Alert | undefined> {
    return this.alerts.get(id);
  }

  async createAlert(insertAlert: InsertAlert): Promise<Alert> {
    const id = randomUUID();
    const alert: Alert = { 
      ...insertAlert, 
      id,
      timestamp: new Date()
    };
    this.alerts.set(id, alert);
    return alert;
  }

  async getAlertsTimeline(): Promise<any[]> {
    // Generate timeline data for charts
    const now = new Date();
    const hours = [];

    for (let i = 6; i >= 0; i--) {
      const time = new Date(now.getTime() - i * 4 * 60 * 60 * 1000);
      const timeStr = time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });

      const alerts = Array.from(this.alerts.values()).filter(alert => {
        const alertTime = alert.timestamp || new Date();
        return alertTime >= new Date(time.getTime() - 4 * 60 * 60 * 1000) && 
               alertTime < new Date(time.getTime() + 4 * 60 * 60 * 1000);
      });

      hours.push({
        time: timeStr,
        critical: alerts.filter(a => a.severity === 'critical').length,
        high: alerts.filter(a => a.severity === 'high').length,
        medium: alerts.filter(a => a.severity === 'medium').length,
        low: alerts.filter(a => a.severity === 'low').length,
      });
    }

    return hours;
  }

  // Vulnerability methods
  async getVulnerabilities(): Promise<Vulnerability[]> {
    return Array.from(this.vulnerabilities.values());
  }

  async getVulnerability(id: string): Promise<Vulnerability | undefined> {
    return this.vulnerabilities.get(id);
  }

  async createVulnerability(insertVulnerability: InsertVulnerability): Promise<Vulnerability> {
    const id = randomUUID();
    const vulnerability: Vulnerability = { 
      ...insertVulnerability, 
      id,
      detectedAt: new Date()
    };
    this.vulnerabilities.set(id, vulnerability);
    return vulnerability;
  }

  async getVulnerabilityDistribution(): Promise<any[]> {
    const vulns = Array.from(this.vulnerabilities.values());
    return [
      { name: 'Critical', value: vulns.filter(v => v.severity === 'critical').length, color: '#EF4444' },
      { name: 'High', value: vulns.filter(v => v.severity === 'high').length, color: '#F59E0B' },
      { name: 'Medium', value: vulns.filter(v => v.severity === 'medium').length, color: '#FCD34D' },
      { name: 'Low', value: vulns.filter(v => v.severity === 'low').length, color: '#10B981' },
    ];
  }

  // Compliance methods
  async getComplianceResults(): Promise<ComplianceResult[]> {
    return Array.from(this.complianceResults.values());
  }

  async createComplianceResult(insertResult: InsertComplianceResult): Promise<ComplianceResult> {
    const id = randomUUID();
    const result: ComplianceResult = { 
      ...insertResult, 
      id,
      lastChecked: new Date()
    };
    this.complianceResults.set(id, result);
    return result;
  }

  async getComplianceOverview(): Promise<ComplianceFrameworkScore[]> {
    const results = Array.from(this.complianceResults.values());
    const frameworks = ['PCI_DSS', 'GDPR', 'HIPAA', 'NIST_800_53'];

    return frameworks.map(framework => {
      const frameworkResults = results.filter(r => r.framework === framework);
      const passed = frameworkResults.filter(r => r.status === 'pass').length;
      const total = frameworkResults.length || 1; // Avoid division by zero
      const percentage = Math.round((passed / total) * 100);

      return {
        framework,
        score: passed,
        total,
        percentage
      };
    });
  }

  // MITRE ATT&CK methods
  async getMitreAttackData(): Promise<MitreAttackData[]> {
    return Array.from(this.mitreAttackData.values());
  }

  async createMitreAttackData(insertData: InsertMitreAttack): Promise<MitreAttackData> {
    const id = randomUUID();
    const data: MitreAttackData = { 
      ...insertData, 
      id,
      lastSeen: new Date()
    };
    this.mitreAttackData.set(id, data);
    return data;
  }

  // Dashboard methods
  async getDashboardMetrics(): Promise<DashboardMetrics> {
    const agents = Array.from(this.agents.values());
    const alerts = Array.from(this.alerts.values());
    const vulnerabilities = Array.from(this.vulnerabilities.values());
    const complianceOverview = await this.getComplianceOverview();

    const avgCompliance = complianceOverview.reduce((sum, f) => sum + f.percentage, 0) / complianceOverview.length;

    return {
      activeAgents: agents.filter(a => a.status === 'active').length,
      criticalAlerts: alerts.filter(a => a.severity === 'critical').length,
      vulnerabilities: vulnerabilities.length,
      complianceScore: Math.round(avgCompliance)
    };
  }
}

export const storage = new MemStorage();

