import { Link, useLocation } from "wouter";
import { 
  Gauge, 
  TrendingUp, 
  AlertTriangle, 
  Bug, 
  Shield, 
  Target, 
  CreditCard, 
  UserCheck, 
  Hospital, 
  Bookmark, 
  Server, 
  Settings, 
  Code, 
  FlaskConical 
} from "lucide-react";
import { cn } from "@/lib/utils";

export function Sidebar() {
  const [location] = useLocation();

  const getRouteFromId = (id: string) => {
    if (id === "overview") return "/";
    return `/${id}`;
  };

  const getActiveId = () => {
    if (location === "/") return "overview";
    return location.substring(1);
  };

  const menuItems = [
    {
      section: "Dashboard",
      items: [
        { id: "overview", icon: Gauge, label: "Overview", active: true },
        { id: "analytics", icon: TrendingUp, label: "Analytics" },
      ]
    },
    {
      section: "Security",
      items: [
        { id: "alerts", icon: AlertTriangle, label: "Alerts", badge: "24" },
        { id: "vulnerabilities", icon: Bug, label: "Vulnerabilities" },
