                  <item.icon className="w-4 h-4" />
                  <span className="flex-1 text-left">{item.label}</span>
                  {(item as any).badge && (
                    <span className="bg-red-500 text-xs px-2 py-1 rounded-full">
                      {(item as any).badge}
                    </span>
                  )}
                  {(item as any).count && (
                    <span className="text-xs text-gray-500">
                      {(item as any).count}
                    </span>
                  )}
